const buttons=document.querySelectorAll(".packages2-button");buttons.forEach(n=>{n.addEventListener("click",()=>{const i=n.id.replace("toggleButton","packagesList"),r=document.getElementById(i),t=n.getAttribute("aria-expanded")==="true";r.classList.toggle("expanded",!t);n.setAttribute("aria-expanded",(!t).toString());n.textContent=t?"Show more":"Show less"})});